import React, { useState } from 'react';

const SiparisFormu = () => {
  const[formData, setFormData]=useState;

  const handleSubmit = (e) => {
    e.preventDefault();
    axios
    .post(
      "https://reqres.in/api/users",formData)
      .then(res => {
      console.log(res.data); 
      setFormData(formData);
    })
    .catch(err => {
      console.log(err);
    });
  };

  return (
    <div>
      <h1>Sipariş Formu</h1>
      <form onSubmit={handleSubmit}>
        <label htmlFor="urun">Ürün:</label>
        <select id="urun" name="urun">
          <option value="urun1">Ürün 1</option>
          <option value="urun2">Ürün 2</option>
          <option value="urun3">Ürün 3</option>
        </select>

        <label htmlFor="ad">Adınız:</label>
        <input type="text" id="ad" name="ad" required />

        <label htmlFor="soyad">Soyadınız:</label>
        <input type="text" id="soyad" name="soyad" required />

        <label htmlFor="adres">Adres:</label>
        <textarea id="adres" name="adres" rows="4" cols="50" required></textarea>

        <button type="submit">Sipariş Ver</button>
      </form>
    </div>
  );
};

export default SiparisFormu;
