import React, { useState } from 'react';
import axios from 'axios';
import { useHistory } from 'react-router-dom';

const SiparisFormu = () => {
  const [formData, setFormData] = useState({
    urun: '',
    ad: '',
    soyad: '',
    adres: ''
  });

  const history = useHistory();

  const handleSubmit = (e) => {
    e.preventDefault();
    axios
      .post("https://reqres.in/api/users", formData)
      .then(res => {
        console.log(res.data);
        // İşlem tamamlandıktan sonra sipariş alındı sayfasına yönlendir
        history.push('/siparis-alindi');
      })
      .catch(err => {
        console.log(err);
      });
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  return (
    <div>
      <h1>Sipariş Formu</h1>
      <form onSubmit={handleSubmit}>
        <label htmlFor="urun">Ürün:</label>
        <select id="urun" name="urun" value={formData.urun} onChange={handleChange} required>
          <option value="">Ürün Seçin</option>
          <option value="urun1">Ürün 1</option>
          <option value="urun2">Ürün 2</option>
          <option value="urun3">Ürün 3</option>
        </select>

        <label htmlFor="ad">Adınız:</label>
        <input type="text" id="ad" name="ad" value={formData.ad} onChange={handleChange} required />

        <label htmlFor="soyad">Soyadınız:</label>
        <input type="text" id="soyad" name="soyad" value={formData.soyad} onChange={handleChange} required />

        <label htmlFor="adres">Adres:</label>
        <textarea id="adres" name="adres" rows="4" cols="50" value={formData.adres} onChange={handleChange} required></textarea>

        <button type="submit">Sipariş Ver</button>
      </form>
    </div>
  );
};

export default SiparisFormu;
