import React, { useState } from 'react';
import axios from 'axios';
import { useHistory } from 'react-router-dom';
import * as Yup from "yup";

const SiparisFormu = () => {
  const [formData, setFormData] = useState({
    boyut: '',
    hamurKalınlıgı: '',
    malzemeler: [],
    siparisNotu: '',
    adet: 1,
  });

  const [formErrs, setFormErrs] = useState({
    boyut: '',
    hamurKalınlıgı: '',
    malzemeler: [],
    siparisNotu: '',
    adet: 1,
  });
const ekMalzemeler=[ "Pepperoni", "Domates", "Biber", "Sosis", "Mısır", "Sucuk", "Kanada Jambonu", "Ananas", "Tavuk Izgara", "Jalepeno", "Kabak", "Soğan", "Sarımsak"]

  const [valid, setValid] = useState(false);

  const history = useHistory();

  const handleSubmit = (e) => {
    e.preventDefault();
    axios
      .post("https://reqres.in/api/users", formData)
      .then(res => {
        console.log(res.data);
        history.push('/siparis-alindi');
      })
      .catch(err => {
        console.log(err);
      });
  };

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;

    if (type === 'checkbox') {
        let malzemeler = [...formData.malzemeler];
        if (checked) {
          if (malzemeler.length <= 10) {
            malzemeler.push(value);
          }
        } else {
          malzemeler = malzemeler.filter(item => item !== value);
        }
      setFormData(data => ({
        ...data,
        malzemeler
      }));
    } else {
       Yup.reach(formSchema, name)
    .validate(value)
    .then((valid) => {
      setFormErrs({ ...formErrs, [name]: "" });
    })
    .catch((err) => {
      setFormErrs({ ...formErrs, [name]: err.errors[0] });
    });

      setFormData({ ...formData, [name]: value });
    }
  };
  const formSchema = Yup.object().shape({
    boyut: Yup.boolean().oneOf([true], "Boyut seçmelisiniz."),
    hamurKalınlıgı: Yup.boolean().oneOf([true], "Boyut seçmelisiniz."),
    malzemeler:Yup.boolean().oneOf([true], "Boyut seçmelisiniz."),
    siparisNotu: Yup.string(),
    adet: Yup.boolean().oneOf([true], "Boyut seçmelisiniz."),
  });

  const handleAdetArttir = () => {
    setFormData({
      ...formData,
      adet: formData.adet + 1
    });
  };

  const handleAdetEksilt = () => {
    setFormData({
      ...formData,
      adet: formData.adet > 1 ? formData.adet - 1 : 1
    });
  };

  const hesaplaSecimlerTutari = () => {
    const ekMalzemeFiyati = 5;
    const toplamFiyat = ekMalzemeFiyati * formData.malzemeler.length
    return toplamFiyat
  };
  const hesaplaSiparisTutari = () => {
    const birimFiyat = 10;
    const toplamFiyat = (birimFiyat + hesaplaSecimlerTutari()) * formData.adet;
    return toplamFiyat
  };


  return (
    <div>
      <h1>Sipariş Formu</h1>
      <form onSubmit={handleSubmit}>
        <label htmlFor="boyut">Pizza Boyutu:</label>
        <div>
          <label>
            <input type="checkbox" name="boyut" value="büyük" onChange={handleChange} />
            Büyük
          </label>
          <label>
            <input type="checkbox" name="boyut" value="orta" onChange={handleChange} />
            Orta
          </label>
          <label>
            <input type="checkbox" name="boyut" value="küçük" onChange={handleChange} />
            Küçük
          </label>
        </div>

        <label htmlFor="hamurKalınlığı">Hamur Kalınlığı:</label>
        <select id="hamurKalınlığı" name="hamurKalınlığı" value={formData.hamurKalınlığı} onChange={handleChange} required>
          <option value="">Hamur Kalınlığı Seçin</option>
          <option value="ince">İnce Kenar</option>
          <option value="kalın">Kalın Kenar</option>
          <option value="standart">Standart</option>
        </select>

        <label>Ek Malzemeler:</label>
        {ekMalzemeler.map((malzeme,index)=>(
        <div key={index}>
          <label>
            <input type="checkbox" name={malzeme} value={malzeme} onChange={handleChange}/>
            {malzeme}
          </label>
        </div>
        ))}
        <label htmlFor="siparisNotu">Sipariş Notu:</label>
        <input type="text" id="siparisNotu" name="siparisNotu" value={formData.siparisNotu} onChange={handleChange} />
        <label htmlFor="adet">Adet:</label>
        <div>
          <button type="button" onClick={handleAdetEksilt}>-</button>
          <input type="number" id="adet" name="adet" value={formData.adet} onChange={handleChange} min="1" />
          <button type="button" onClick={handleAdetArttir}>+</button>
        </div>

        <div className="siparis-tutari">
          <h3>Sipariş Toplamı</h3>
          <p> Seçimler: {hesaplaSecimlerTutari()} TL</p>
          <p> Toplam: {hesaplaSiparisTutari()} TL</p>
        </div>

        <button type="submit">Sipariş Ver</button>
      </form>
    </div>
  );
};

export default SiparisFormu;
