import React from 'react';
import "./Success.css";

const Success =({ hesaplaSecimlerTutari, hesaplaSiparisTutari, formData, pizzaBilgileri }) => {

  return (
    <div className='success-page'>
      <h1 style={{fontFamily: "Barlow", width: "100%", textAlign: "center", fontSize: "2rem", marginTop: "5%" }}>Teknolojik Yemekler</h1>
      <p style={{ color: "#FDC913", fontFamily: "Satisfy", width: "100%", textAlign: "center", fontSize: "2rem", marginBottom: "0%" }}>lezzetin yolda</p>
      <p style={{  fontFamily: "Barlow", width: "100%", textAlign: "center", fontSize: "3rem", marginBottom: "0%" }}>SİPARİŞİNİZ ALINDI.</p>
      <h4>{pizzaBilgileri}</h4>
      <div className='pizza'>
        <p>Sipariş Notu: {formData && formData.siparisNotu}</p>
        <p>Adet: {formData && formData.adet}</p>
        <p>Boyut: {formData && formData.boyut}</p>
        <p>Hamur: {formData && formData.hamur}</p>
        <p>Malzemeler: {formData && formData.malzemeler.join(', ')}</p>
      </div>
      <div className="siparis-tutari" style={{background:"#CE2829"}}>
        <h3>Sipariş Toplamı</h3>
        <p> Seçimler: {hesaplaSecimlerTutari} ₺</p>
        <p> Toplam:{hesaplaSiparisTutari} ₺</p>
      </div>
    </div>

  );
};

export default Success;
