import React from 'react';
import { useEffect } from 'react';

const Success =({ hesaplaSecimlerTutari, hesaplaSiparisTutari, formData }) => {
  let  fiyat = 0;
  let seçim =0;
useEffect(()=>{
 fiyat = hesaplaSecimlerTutari
 seçim =hesaplaSiparisTutari
},[formData])
  return (
    <div className='success-page'>
      <h1>Teknolojik Yemekler</h1>
      <p>lezzetin yolda</p>
      <p>Siparişiniz alındı.</p>
      <h4>a</h4>
      <div className='pizza'>
        <p>Sipariş Notu: {formData && formData.siparisNotu}</p>
        <p>Adet: {formData && formData.adet}</p>
        <p>Boyut: {formData && formData.boyut}</p>
        <p>Hamur: {formData && formData.hamur}</p>
        <p>Malzemeler: {formData && formData.malzemeler.join(', ')}</p>
      </div>
      <div className="siparis-tutari">
        <h3>Sipariş Toplamı</h3>
        <p> Seçimler: {seçim} ₺</p>
        <p> Toplam:{fiyat} ₺</p>
      </div>
    </div>

  );
};

export default Success;
