import React from 'react';

const Success = ({ hesaplaSecimlerTutari, hesaplaSiparisTutari}) => {

  return (
    <div>
      <h1>Siparişiniz Alındı</h1>
      <p>Siparişiniz başarıyla alındı. En kısa sürede size ulaştırılacaktır.</p>
      <div className="siparis-tutari">
        <h3>Sipariş Toplamı</h3>
        <p> Seçimler: {hesaplaSecimlerTutari()} ₺</p>
        <p> Toplam: {hesaplaSiparisTutari()} ₺</p>
      </div>
    </div>

  );
};

export default Success;
