import React from 'react';
import { useEffect } from 'react';

const Success = ({ hesaplaSecimlerTutari, hesaplaSiparisTutari, formData }) => {

let secimlerFiyat= 0;
let toplamFiyat= 0;

useEffect(() => {
  secimlerFiyat = hesaplaSecimlerTutari();
  toplamFiyat = hesaplaSiparisTutari();
}, [formData]);

  return (
    <div>
      <h1>Siparişiniz Alındı</h1>
      <p>Siparişiniz başarıyla alındı. En kısa sürede size ulaştırılacaktır.</p>
      <div className="siparis-tutari">
        <h3>Sipariş Toplamı</h3>
        <p> Seçimler: {secimlerFiyat} ₺</p>
        <p> Toplam: {toplamFiyat} ₺</p>
      </div>
    </div>

  );
};

export default Success;
