import React, { useEffect } from 'react';
import { useHistory } from "react-router-dom";

const Redirect = () =>{

    const history= useHistory();
        setTimeout(()=>useEffect(()=>{
            history.push("/order-pizza")
        }), 5000)

    return (
        <div style={{ backgroundPosition:"center", backgroundSize:"cover", height:"100vh", backgroundColor:"#CE2829," }}>
        <h1 style={{ width:"60%", margin: "auto", color:"white" }}>Sipariş Sayfasına Yönlendiriliyorsunuz.</h1>
        </div>
    )
}
export default Redirect
