import React, { useState } from 'react';
import axios from 'axios';
import { useHistory } from 'react-router-dom';

const SiparisFormu = () => {
  const [formData, setFormData] = useState({
    urun: '',
    boyut: '',
    hamurKalınlıgı: '',
    ad: '',
    soyad: '',
    adres: '',
    malzemeler: [],
    siparisNotu: ''
  });

  const history = useHistory();

  const handleSubmit = (e) => {
    e.preventDefault();
    axios
      .post("https://reqres.in/api/users", formData)
      .then(res => {
        console.log(res.data);
        history.push('/siparis-alindi');
      })
      .catch(err => {
        console.log(err);
      });
  };

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;

    if (type === 'checkbox') {
      let malzemeler = [...formData.malzemeler];
      if (checked) {
        malzemeler.push(value);
      } else {
        malzemeler = malzemeler.filter(item => item !== value);
      }
      setFormData(prevState => ({
        ...prevState,
        malzemeler
      }));
    } else {
      setFormData(data => ({
        ...data,
        [name]: value
      }));
    }
  };

  return (
    <div>
      <h1>Sipariş Formu</h1>
      <form onSubmit={handleSubmit}>
        <label htmlFor="urun">Ürün:</label>
        <select id="urun" name="urun" value={formData.urun} onChange={handleChange} required>
          <option value="">Ürün Seçin</option>
          <option value="urun1">Ürün 1</option>
          <option value="urun2">Ürün 2</option>
          <option value="urun3">Ürün 3</option>
        </select>

        <label htmlFor="boyut">Pizza Boyutu:</label>
        <div>
          <label>
            <input type="checkbox" name="boyut" value="büyük" onChange={handleChange} />
            Büyük
          </label>
          <label>
            <input type="checkbox" name="boyut" value="orta" onChange={handleChange} />
            Orta
          </label>
          <label>
            <input type="checkbox" name="boyut" value="küçük" onChange={handleChange} />
            Küçük
          </label>
        </div>

        <label htmlFor="hamurKalınlığı">Hamur Kalınlığı:</label>
        <select id="hamurKalınlığı" name="hamurKalınlığı" value={formData.hamurKalınlığı} onChange={handleChange} required>
          <option value="">Hamur Kalınlığı Seçin</option>
          <option value="ince">İnce Kenar</option>
          <option value="kalın">Kalın Kenar</option>
          <option value="standart">Standart</option>
        </select>

        <label>Ek Malzemeler:</label>
        <div>
          <label>
            <input type="checkbox" name="malzemeler" value="malzeme1" onChange={handleChange} />
            Malzeme 1
          </label>
          <label>
            <input type="checkbox" name="malzemeler" value="malzeme2" onChange={handleChange} />
            Malzeme 2
          </label>
          <label>
            <input type="checkbox" name="malzemeler" value="malzeme3" onChange={handleChange} />
            Malzeme 3
          </label>
          <label>
            <input type="checkbox" name="malzemeler" value="malzeme4" onChange={handleChange} />
            Malzeme 4
          </label>
          <label>
            <input type="checkbox" name="malzemeler" value="malzeme5" onChange={handleChange} />
            Malzeme 5
          </label>
          <label>
            <input type="checkbox" name="malzemeler" value="malzeme6" onChange={handleChange} />
            Malzeme 6
          </label>
          <label>
            <input type="checkbox" name="malzemeler" value="malzeme7" onChange={handleChange} />
            Malzeme 7
          </label>
          <label>
            <input type="checkbox" name="malzemeler" value="malzeme8" onChange={handleChange} />
            Malzeme 8
          </label>
          <label>
            <input type="checkbox" name="malzemeler" value="malzeme9" onChange={handleChange} />
            Malzeme 9
          </label>
          <label>
            <input type="checkbox" name="malzemeler" value="malzeme10" onChange={handleChange} />
            Malzeme 10
          </label>
          <label>
            <input type="checkbox" name="malzemeler" value="malzeme11" onChange={handleChange} />
            Malzeme 11
          </label>
          <label>
            <input type="checkbox" name="malzemeler" value="malzeme12" onChange={handleChange} />
            Malzeme 12
          </label>
          <label>
            <input type="checkbox" name="malzemeler" value="malzeme13" onChange={handleChange} />
            Malzeme 13
          </label>
          <label>
            <input type="checkbox" name="malzemeler" value="malzeme14" onChange={handleChange} />
            Malzeme 14
          </label>
        </div>

        <label htmlFor="siparisNotu">Sipariş Notu:</label>
        <input type="text" id="siparisNotu" name="siparisNotu" value={formData.siparisNotu} onChange={handleChange} />

        <button type="submit">Sipariş Ver</button>
      </form>
    </div>
  );
};

export default SiparisFormu;
