import React, { useState } from 'react';
import axios from 'axios';
import { useHistory } from 'react-router-dom';
import * as Yup from "yup";
import Success from './Success';
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom';

const OrderForm = () => {
    const [formData, setFormData] = useState({
        boyut: '',
        hamur: '',
        malzemeler: [],
        siparisNotu: '',
        adet: 1,
    });

    const [formErrs, setFormErrs] = useState({
        boyut: '',
        hamur: '',
        malzemeler: [],
        siparisNotu: '',
        adet: 1,
    });
    const ekMalzemeler = ["Pepperoni", "Domates", "Biber", "Sosis", "Mısır", "Sucuk", "Kanada Jambonu", "Ananas", "Tavuk Izgara", "Jalepeno", "Kabak", "Soğan", "Sarımsak"]

    const [valid, setValid] = useState(false);

    const history = useHistory();

    const handleSubmit = (e) => {
        e.preventDefault();
        Yup
            .validateAt(name, formData, formSchema)
            .then(() => {
                axios
                    .post("https://reqres.in/api/users", formData)
                    .then(res => {
                        console.log(res.data);
                        history.push('/order-success');
                    })
                    .catch(err => {
                        console.log(err);
                    });
            })
            .catch((err) => {
                setFormErrs({ ...formErrs, [name]: err.errors[0] });
                console.log(err);
            });
    }


    const handleChange = (e) => {
        const { name, value, type, checked } = e.target;

        if (type === 'checkbox') {
            if (checked) {
                if (checked) {
                    formData.malzemeler.push(value);
                }
            } else {
                formData.malzemeler = formData.malzemeler.filter(item => item !== value);
            }
            setFormData({ ...formData, [name]: value });
            console.log(formData)
        } else {
            Yup.reach(formSchema, name)
                .validate(value)
                .then(() => {
                    setFormErrs({ ...formErrs, [name]: "" });
                })
                .catch((err) => {
                    setFormErrs({ ...formErrs, [name]: err.errors[0] });
                });

            setFormData({ ...formData, [name]: value });
        }
    };
    const formSchema = Yup.object().shape({
        boyut: Yup.boolean().oneOf([true], "Boyut seçmelisiniz."),
        hamur: Yup.boolean().oneOf([true], "Boyut seçmelisiniz."),
        malzemeler: Yup.array().max(10, "10 malzemeden fazlasını seçemezsiniz."),
        siparisNotu: Yup.string(),
        adet: Yup.number().min(1, "Minimum 1 adet ürün seçmelisiniz.")
    });

    const handleAdetArttir = () => {
        setFormData({
            ...formData,
            adet: formData.adet + 1
        });
    };

    const handleAdetEksilt = () => {
        setFormData({
            ...formData,
            adet: formData.adet > 1 ? formData.adet - 1 : 1
        });
    };

    const hesaplaSecimlerTutari = () => {
        const ekMalzemeFiyati = 5;
        const toplam = ekMalzemeFiyati * formData.malzemeler.length
        return toplam
    };
    const hesaplaSiparisTutari = () => {
        const birimFiyat = 10;
        const toplamFiyat = (birimFiyat + hesaplaSecimlerTutari()) * formData.adet;
        return toplamFiyat
    };


    return (
       <div className='order-page'>
        <div className='siparis-formu' >
            <h1>Sipariş Formu</h1>
            <form onSubmit={handleSubmit}>
                <div className='pizza-boyutu'>
                    <label>Pizza Boyutu:</label>
                    <label htmlFor="boyut-kucuk">Küçük</label>
                    <input
                        type="radio"
                        id="boyut-kucuk"
                        name="boyut"
                        value="kucuk"
                        checked={formData.boyut === "kucuk"}
                        onChange={handleChange}
                        required
                    />

                    <label htmlFor="boyut-orta">Orta</label>
                    <input
                        type="radio"
                        id="boyut-orta"
                        name="boyut"
                        value="orta"
                        checked={formData.boyut === "orta"}
                        onChange={handleChange}
                        required
                    />
                    <label htmlFor="boyut-buyuk">Büyük</label>
                    <input
                        type="radio"
                        id="boyut-buyuk"
                        name="boyut"
                        value="buyuk"
                        checked={formData.boyut === "buyuk"}
                        onChange={handleChange}
                        required
                    />
                </div>
                <div className='hamur'>
                    <label htmlFor="hamur">Hamur:</label>
                    <select id="hamur" name="hamur" value={formData.hamur} onChange={handleChange} required>
                        <option value="">Hamur Kalınlığı Seçin</option>
                        <option value="ince">İnce Kenar</option>
                        <option value="kalin">Kalın Kenar</option>
                        <option value="standart">Standart</option>
                    </select>
                </div>
                <div className='ek-malzemeler'>
                    <label>Ek Malzemeler:</label>
                    {ekMalzemeler.map((malzeme, index) => (
                        <label key={index}>
                            <input
                                type="checkbox"
                                id={malzeme}
                                name={malzeme}
                                value={malzeme}
                                onChange={handleChange}
                                checked={formData.malzemeler.includes(malzeme)}
                                disabled={formData.malzemeler.length >= 10 && !formData.malzemeler.includes(malzeme)} />
                            {malzeme}
                        </label>
                    ))}
                </div>
                <div className='siparis-notu'>
                    <label htmlFor="siparisNotu">Sipariş Notu:</label>
                    <input type="text" id="siparisNotu" name="siparisNotu" value={formData.siparisNotu} onChange={handleChange} />
                    <label htmlFor="adet">Adet:</label>
                </div>
                <div className='adet-button'>
                    <button type="button" onClick={handleAdetEksilt}>-</button>
                    <input type="number" id="adet" name="adet" value={formData.adet} onChange={handleChange} min="1" />
                    <button type="button" onClick={handleAdetArttir}>+</button>
                </div>

                <div className="siparis-tutari">
                    <h3>Sipariş Toplamı</h3>
                    <p> Seçimler: {hesaplaSecimlerTutari()} ₺</p>
                    <p> Toplam: {hesaplaSiparisTutari()} ₺</p>
                </div>
                <Router>
      <Switch>
        <Route path="/order-success"><Success hesaplaSecimlerTutari={hesaplaSecimlerTutari} hesaplaSiparisTutari={hesaplaSiparisTutari} formData={formData}/></Route>
      </Switch>
    </Router>
                <button className="submit-button" type="submit">Sipariş Ver</button>
            </form>
        </div>
        </div>
    );
};

export default OrderForm;
