import React, { useState } from 'react';
import axios from 'axios';
import { useHistory } from 'react-router-dom';
import * as Yup from "yup";

const SiparisFormu = () => {
  const [formData, setFormData] = useState({
    boyut: '',
    hamurKalınlıgı: '',
    malzemeler: [],
    siparisNotu: '',
    adet: 1,
  });

  const [formErrs, setFormErrs] = useState({
    boyut: '',
    hamurKalınlıgı: '',
    malzemeler: [],
    siparisNotu: '',
    adet: 1,
  });

  const [valid, setValid] = useState(false);

  const history = useHistory();

  const handleSubmit = (e) => {
    e.preventDefault();
    axios
      .post("https://reqres.in/api/users", formData)
      .then(res => {
        console.log(res.data);
        history.push('/siparis-alindi');
      })
      .catch(err => {
        console.log(err);
      });
  };

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;

    if (type === 'checkbox') {
      let malzemeler = [...formData.malzemeler];
      if (checked) {
        malzemeler.push(value);
      } else {
        malzemeler = malzemeler.filter(item => item !== value);
      }
      setFormData(data => ({
        ...data,
        malzemeler
      }));
    } else {
       Yup.reach(formSchema, name)
    .validate(value)
    .then((valid) => {
      setFormErrs({ ...formErrs, [name]: "" });
    })
    .catch((err) => {
      setFormErrs({ ...formErrs, [name]: err.errors[0] });
    });

      setFormData({ ...formData, [name]: value });
    }
  };
  const formSchema = Yup.object().shape({
    boyut: Yup.boolean().oneOf([true], "Boyut seçmelisiniz."),
    hamurKalınlıgı: Yup.boolean().oneOf([true], "Boyut seçmelisiniz."),
    malzemeler:Yup.boolean().oneOf([true], "Boyut seçmelisiniz."),
    siparisNotu: Yup.string(),
    adet: Yup.boolean().oneOf([true], "Boyut seçmelisiniz."),
  });

  const handleAdetArttir = () => {
    setFormData({
      ...formData,
      adet: formData.adet + 1
    });
  };

  const handleAdetEksilt = () => {
    setFormData({
      ...formData,
      adet: formData.adet > 1 ? formData.adet - 1 : 1
    });
  };

  const hesaplaSecimlerTutari = () => {
    const ekMalzemeFiyati = 5;
    const toplamFiyat = ekMalzemeFiyati * formData.malzemeler.length
    return toplamFiyat
  };
  const hesaplaSiparisTutari = () => {
    const birimFiyat = 10;
    const toplamFiyat = (birimFiyat + hesaplaSecimlerTutari()) * formData.adet;
    return toplamFiyat
  };


  return (
    <div>
      <h1>Sipariş Formu</h1>
      <form onSubmit={handleSubmit}>
        <label htmlFor="boyut">Pizza Boyutu:</label>
        <div>
          <label>
            <input type="checkbox" name="boyut" value="büyük" onChange={handleChange} />
