          <option value="ince">İnce Kenar</option>
          <option value="kalın">Kalın Kenar</option>
          <option value="standart">Standart</option>
        </select>

        <label>Ek Malzemeler:</label>
        <div>
          <label>
            <input type="checkbox" name="malzemeler" value="malzeme1" onChange={handleChange}  checked={formData.malzemeler.includes(malzeme)}
            disabled={formData.malzemeler.length >= 10 && !formData.malzemeler.includes(malzeme)} />
            Malzeme 1
          </label>
          <label>
            <input type="checkbox" name="malzemeler" value="malzeme2" onChange={handleChange} checked={formData.malzemeler.includes(malzeme)}
            disabled={formData.malzemeler.length >= 10 && !formData.malzemeler.includes(malzeme)}/>
            Malzeme 2
          </label>
          <label>
            <input type="checkbox" name="malzemeler" value="malzeme3" onChange={handleChange} checked={formData.malzemeler.includes(malzeme)}
            disabled={formData.malzemeler.length >= 10 && !formData.malzemeler.includes(malzeme)}/>
            Malzeme 3
          </label>
          <label>
            <input type="checkbox" name="malzemeler" value="malzeme4" onChange={handleChange} checked={formData.malzemeler.includes(malzeme)}
            disabled={formData.malzemeler.length >= 10 && !formData.malzemeler.includes(malzeme)}/>
            Malzeme 4
          </label>
          <label>
            <input type="checkbox" name="malzemeler" value="malzeme5" onChange={handleChange} checked={formData.malzemeler.includes(malzeme)}
            disabled={formData.malzemeler.length >= 10 && !formData.malzemeler.includes(malzeme)}/>
            Malzeme 5
          </label>
          <label>
            <input type="checkbox" name="malzemeler" value="malzeme6" onChange={handleChange} checked={formData.malzemeler.includes(malzeme)}
            disabled={formData.malzemeler.length >= 10 && !formData.malzemeler.includes(malzeme)}/>
            Malzeme 6
          </label>
          <label>
            <input type="checkbox" name="malzemeler" value="malzeme7" onChange={handleChange} checked={formData.malzemeler.includes(malzeme)}
            disabled={formData.malzemeler.length >= 10 && !formData.malzemeler.includes(malzeme)}/>
            Malzeme 7
          </label>
          <label>
            <input type="checkbox" name="malzemeler" value="malzeme8" onChange={handleChange} checked={formData.malzemeler.includes(malzeme)}
            disabled={formData.malzemeler.length >= 10 && !formData.malzemeler.includes(malzeme)}/>
            Malzeme 8
          </label>
          <label>
            <input type="checkbox" name="malzemeler" value="malzeme9" onChange={handleChange} checked={formData.malzemeler.includes(malzeme)}
            disabled={formData.malzemeler.length >= 10 && !formData.malzemeler.includes(malzeme)}/>
            Malzeme 9
          </label>
          <label>
            <input type="checkbox" name="malzemeler" value="malzeme10" onChange={handleChange} checked={formData.malzemeler.includes(malzeme)}
            disabled={formData.malzemeler.length >= 10 && !formData.malzemeler.includes(malzeme)}/>
            Malzeme 10
          </label>
          <label>
            <input type="checkbox" name="malzemeler" value="malzeme11" onChange={handleChange} checked={formData.malzemeler.includes(malzeme)}
            disabled={formData.malzemeler.length >= 10 && !formData.malzemeler.includes(malzeme)}/>
            Malzeme 11
          </label>
          <label>
            <input type="checkbox" name="malzemeler" value="malzeme12" onChange={handleChange} checked={formData.malzemeler.includes(malzeme)}
            disabled={formData.malzemeler.length >= 10 && !formData.malzemeler.includes(malzeme)}/>
            Malzeme 12
          </label>
          <label>
            <input type="checkbox" name="malzemeler" value="malzeme13" onChange={handleChange} checked={formData.malzemeler.includes(malzeme)}
            disabled={formData.malzemeler.length >= 10 && !formData.malzemeler.includes(malzeme)}/>
            Malzeme 13
          </label>
          <label>
            <input type="checkbox" name="malzemeler" value="malzeme14" onChange={handleChange} checked={formData.malzemeler.includes(malzeme)}
            disabled={formData.malzemeler.length >= 10 && !formData.malzemeler.includes(malzeme)}/>
            Malzeme 14
          </label>
        </div>
        <label htmlFor="siparisNotu">Sipariş Notu:</label>
        <input type="text" id="siparisNotu" name="siparisNotu" value={formData.siparisNotu} onChange={handleChange} />
        <label htmlFor="adet">Adet:</label>
        <div>
          <button type="button" onClick={handleAdetEksilt}>-</button>
          <input type="number" id="adet" name="adet" value={formData.adet} onChange={handleChange} min="1" />
          <button type="button" onClick={handleAdetArttir}>+</button>
        </div>

        <div className="siparis-tutari">
          <h3>Sipariş Toplamı</h3>
          <p> Seçimler: {hesaplaSecimlerTutari()} TL</p>
          <p> Toplam: {hesaplaSiparisTutari()} TL</p>
        </div>

        <button type="submit">Sipariş Ver</button>
      </form>
    </div>
  );
};

export default SiparisFormu;
